package ps2739;

import java.util.Scanner;

public class Jeongwon_2739 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int t;

		Scanner scanner = new Scanner(System.in);

		t = scanner.nextInt();

		for (int i = 1; i < 10; i++) {
			System.out.println(t + " * " + i + " = " + t * i);
		}
	}

}
